import React, { useState } from 'react'
import Memedata from './Memedata'
import Ispass from './Ispass'

const Meme = () => {

    const [meme, setMeme] = useState({
        topText:'',
        buttomText:'',
        randomImage:'https://i.imgflip.com/1bik.jpg'
    })

    const [memeImage, setMemeImage] = useState(Memedata);
    const GetRandomeArray = (e) => {
        e.preventDefault();
        const memeArray = memeImage.data.memes;
        const randomeNumber = Math.floor(Math.random() * memeArray.length)
        // setUrl(memeArray[randomeNumber].url);
        const url = memeArray[randomeNumber].url
        // console.log(url);

        setMeme(meme => ({
            ...meme,
            randomImage:url
        }))
    }

    // hooks handling using array

    // const [thingsArray, setThingsArray] =  useState(['Thing1, Thing 2']);

    // const HandleArray = (e) => {
    //     e.preventDefault();
    //     setThingsArray(thingsArray =>{ 
    //         return [...thingsArray, `Things${thingsArray.length +1}`]})
    //     console.log(thingsArray);
    // }

    // const ThingsElements = thingsArray.map(thing => <p key={thing}>{thing}</p>)



    // object toggle state function using object
    const [toggle, setToggle] = useState({
        name:'Hasaan',
        fName:'Basharat Ali',
        isPass:false
    })
    const handleIsPass = () => {
        setToggle(toggle => ({ ...toggle,isPass: !toggle.isPass
}))
    }






    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="col-12 mt-5 ">
                        {/* {ThingsElements} */}

                        <form className=''>


                            <div className="d-flex">
                                <div className="mb-3 mx-5 col-5">
                                    <input placeholder='Top Text' type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />

                                </div>
                                <div className="mb-3  second-input col-5">
                                    <input placeholder='Buttom Text' type="password" className="form-control" id="exampleInputPassword1" />
                                </div><br />
                            </div>
                            <button onClick={GetRandomeArray} className='w-100 form-btn' type="submit">Get A New Image </button>
                        </form>
                        <div className='meme-image mx-auto mt-5'>
                            <img src={meme.randomImage} alt="" />
                        </div>
                    </div>
                </div>
            </div>
            {/* <button onClick={HandleArray}>HandleArray</button> */}
            <div>
                <h1>Name :{toggle.name}</h1>
                <h2>Father Name :{toggle.fName}</h2>
                {/* <h5 onClick={handleIsPass}>Result:{checkPass}</h5> */}
                <Ispass toggle={toggle}/>
            </div>

        </div>
    )
}

export default Meme
