        e.preventDefault();
        const memeArray = Memedata.data.memes;
        const randomeNumber = Math.floor(Math.random() * memeArray.length)
         url = memeArray[randomeNumber].url;
        console.log(url);
    }
    const date = new Date();
    const hour = date.getHours()
    const getDate = (name) => {
        e.preventDefault()
        // console.log(`hour: ${hour}`);
