    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="col-12 mt-5 ">
                        <img src={url} alt="" />
                        <form className=''>
            {/* {ThingsElements} */}

                            <div className="d-flex">
                                <div className="mb-3 mx-5 col-5">
                                    <input placeholder='Top Text' type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />

                                </div>
                                <div className="mb-3  second-input col-5">
                                    <input placeholder='Buttom Text' type="password" className="form-control" id="exampleInputPassword1" />
                                </div><br />
                            </div>
                            <button onClick={GetRandomeArray} className='w-100 form-btn' type="submit">Get A New Image </button>
                            <button onClick={getDate} className='btn btn-primary w-100 mt-5'>Array Render</button>

                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Meme
