import React, { useState } from 'react'

const Box = (props) => {
    const [on, setOn] = useState(props.on)

    const style ={
        backgroundColor: on ? "#222222" : 'tranparent',
    }

    const toggle = () => {
        setOn(preState => !preState)
    }

  return (
    <div style={style} onClick={toggle} className="boxes"></div>
  )
}

export default Box
